import React from 'react';
import { View, Text, TextInput, FlatList, Image, TouchableOpacity, ScrollView, StyleSheet, ImageBackground } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';

// Import screens
import ThingsToDoScreen from './screens/ThingsToDo';
import Attraction from './screens/Attractions';
import ItemDetails from './screens/ItemDetails';
import AttractionDetails from './screens/AttractionDetails';
import Cart from './screens/Cart';
import PacakgeOption from './screens/PackageOption';
import BookingPage from './screens/Booking';
import PaymentPage from './screens/Payment';
import PaymentSuccess from './screens/PaymentSucess';
import BookingHistory from './screens/BookingHistory';
import EventPage from'./screens/Event';
import ReviewsScreen from './screens/Review';
import FoodDining from './screens/FoodAndDining';
import AccountScreen from './screens/Account';
import { CartProvider } from './screens/CartContext';
import { BookingHistoryProvider } from './screens/BookingHistoryContext';
import { LogBox } from 'react-native';

LogBox.ignoreLogs(['Got a component with the name cart for the screen cart.']);

interface CategoryButtonProps {
  title: string;
  image: any;
  onPress: () => void;
}

interface BestofPenangItem {
  id: string;
  title: string;
  rating: number;
  booked: string;
  price: string;
  location: string;
  image: any; 
}

interface HomeScreenProps {
  navigation: any; 
}

interface NavItemProps {
  title: string;
  icon: any; 
}

type RootStackParamList = {
  Home: undefined;
  ThingsToDo: undefined;
  Attraction: undefined;
  Cart: undefined;
  PaymentPage: { bookingDetails: BookingDetails };
  ItemDetails: { item: ThingsToDoItem };
  AttractionDetails: { item: AttractionItem };
  BestofPenangDetails: { item: BestofPenangItem };
  PackageOption: undefined;
  BookingPage: { bookItem: BookItem };
  BookingHistory: { bookingHistory: BookingDetails[] };
  PaymentSuccess: undefined;
  EventPage:undefined;
  ReviewsScreen:undefined;
  AccountScreen: undefined;
};

interface AttractionItem {
  id: string;
  image: string;
  title: string;
  rating: number;
  booked: string;
  price: string;
  location: string;
}

interface ThingsToDoItem {
  id: string;
  title: string;
  rating: number;
  booked: string;
  price: string;
  location: string;
  image: string;
  ticketOptions: TicketOption[];
}

interface Traveler {
  type: 'adult' | 'senior' | 'child' | 'infant'; 
  count: number;
}

interface BookItem {
  id: string;
  image: string;
  name: string;
  option: TicketOption; 
  date: string;
  time: string;
  quantity: number;
  travelers: Traveler[]; 
}

interface TicketOption {
  id: string;
  title: string;
  price: number; 
}

interface BookingDetails {
  firstName: string;
  lastName: string;
  myKad: string;
  email: string;
  contact: string;
  subtotal: number;
  ticket: BookItem; 
}

const Stack = createNativeStackNavigator<RootStackParamList>();

const App = () => {
  return (
    <BookingHistoryProvider>
      <CartProvider>
        <NavigationContainer>
          <Stack.Navigator>
            <Stack.Screen name="Home" component={HomeScreen} options={{ headerShown: false }} />
            <Stack.Screen name="ThingsToDo" component={ThingsToDoScreen} options={{ headerShown: false }} />
            <Stack.Screen name="Attraction" component={Attraction} options={{ headerShown: false }} />
            <Stack.Screen name="ItemDetails" component={ItemDetails} options={{ headerShown: false }} />
            <Stack.Screen name="AttractionDetails" component={AttractionDetails} options={{ headerShown: false }} />
            <Stack.Screen name="Cart" component={Cart} options={{ headerShown: false }} />
            <Stack.Screen name="PackageOption" component={PacakgeOption} options={{ headerShown: false }} />
            <Stack.Screen name="BookingPage" component={BookingPage} options={{ headerShown: false }} />
            <Stack.Screen name="PaymentPage" component={PaymentPage} options={{ headerShown: false }} />
            <Stack.Screen name="BookingHistory" component={BookingHistory} options={{ headerShown: false }} />
            <Stack.Screen name="PaymentSuccess" component={PaymentSuccess} options={{ headerShown: false }} />
            <Stack.Screen name="EventPage" component={EventPage} options={{ headerShown: false }} />
            <Stack.Screen name="ReviewsScreen" component={ReviewsScreen} options={{ headerShown: false }} />
            <Stack.Screen name="FoodDining" component={FoodDining} options={{ headerShown: false }} />
            <Stack.Screen name="AccountScreen" component={AccountScreen} options={{ headerShown: false }} />
          </Stack.Navigator>
        </NavigationContainer>
      </CartProvider>
    </BookingHistoryProvider>
  );
};

const HomeScreen: React.FC<HomeScreenProps> = ({ navigation }) => {
  const attractionsData: BestofPenangItem[] = [
    {
      id: '1',
      title: 'Penang 3D Trick Art Museum Ticket',
      rating: 3.5,
      booked: '3K+',
      price: 'RM 15.20',
      image: 'https://image.kkday.com/v2/image/get/w_960%2Cc_fit%2Cq_55%2Ct_webp/s1.kkday.com/product_33217/20210825085605_hS5ki/jpg',
      location: 'Georgetown',
    },
    {
      id: '2',
      title: 'Tech Dome Penang',
      rating: 4.6,
      booked: '6K+',
      price: 'RM 15.00',
      image: 'data:image/jpeg;base64,',
      location: 'Georgetown',
    },
    {
      id: '3',
      title: 'ESCAPE Penang Ticket',
      rating: 4.5,
      booked: '2K+',
      price: 'RM 180.00',
      image: 'data:image/jpeg;base64,',
      location: 'Tanjung Bungah',
    },
  ];

  const renderItem = ({ item }: { item: BestofPenangItem }) => (
    <TouchableOpacity onPress={() => navigation.navigate('AttractionDetails', { item })}>
      <View style={styles.card}>
        <Image source={{ uri: item.image }} style={styles.image} />
        <View style={styles.cardContent}>
          <Text style={styles.cardTitle}>{item.title}</Text>
          <Text style={styles.rating}>★ {item.rating} • {item.booked} booked</Text>
          <Text style={styles.price}>{item.price}</Text>
        </View>
      </View>
    </TouchableOpacity>
  );

  return (
    <ImageBackground source={require('./image/background.png')} style={styles.backgroundImage}>
      <View style={styles.container}>
        <ScrollView style={styles.scrollView} contentContainerStyle={styles.contentContainer} keyboardShouldPersistTaps="handled" nestedScrollEnabled={true}>
          {/* Search Bar */}
          <View style={styles.searchBar}>
            <TextInput style={styles.searchInput} placeholder="Search for attractions" />
            <TouchableOpacity onPress={() => navigation.navigate('Cart')}>
              <Image source={require('./image/Shopping-cart.png')} style={styles.cartIcon} />
            </TouchableOpacity>
          </View>

          {/* Categories */}
          <View style={styles.categories}>
            <CategoryButton
              image={require('./image/ticket.png')}
              title="Things To Do"
              onPress={() => navigation.navigate('ThingsToDo')}
            />
            <CategoryButton
              image={require('./image/spoon-and-fork.png')}
              title="Food & Dining"
              onPress={() => navigation.navigate('FoodDining')}
            />
            <CategoryButton
              image={require('./image/attraction.png')}
              title="Attraction"
              onPress={() => navigation.navigate('Attraction')}
            />
            <CategoryButton
              image={require('./image/event.png')}
              title="Events"
              onPress={() => navigation.navigate('EventPage')}
            />
          </View>

          {/* Best of Penang Section */}
          <View style={styles.headerSection}>
            <Text style={styles.sectionTitle}>The Best of Penang</Text>
            <TouchableOpacity>
              <Text style={styles.seeAll}>See All</Text>
            </TouchableOpacity>
          </View>

          <View style={{ flex: 1 }}>
            <FlatList
              data={attractionsData}
              keyExtractor={(item) => item.id}
              renderItem={renderItem}
            />
          </View>
        </ScrollView>

        {/* Bottom Navigation */}
        <View style={styles.bottomNav}>

        <TouchableOpacity style={styles.iconButton} onPress={() => navigation.navigate('Home')}>
          <View style={{ alignItems: 'center', marginBottom: 5, }}>
            <Image source={require('./image/home-icon.png')} style={styles.iconImage} />
            <Text style={styles.homeNav}>Home</Text>
          </View>
        </TouchableOpacity>

        <TouchableOpacity style={styles.iconButton} onPress={() => navigation.navigate('BookingHistory', { bookingHistory: [] })}>
          <View style={{ alignItems: 'center', marginBottom: 5 }}>
            <Image source={require('./image/trips-icon.png')} style={styles.iconImage} />
            <Text style={styles.homeNav}>Trips</Text>
          </View>
        </TouchableOpacity>

        <TouchableOpacity style={styles.iconButton} onPress={() => navigation.navigate('AccountScreen')}>
          <View style={{ alignItems: 'center', marginBottom: 5, }}>
            <Image source={require('./image/account-icon.png')} style={styles.iconImage} />
            <Text style={styles.homeNav}>Account</Text>
          </View>
        </TouchableOpacity>
      </View>
      </View>
    </ImageBackground>
  );
};

const CategoryButton: React.FC<CategoryButtonProps> = ({ title, image, onPress }) => (
  <TouchableOpacity style={styles.categoryButton} onPress={onPress}>
    <Image source={image} style={styles.categoriesImage} />
    <Text style={styles.categoryButtonText}>{title}</Text>
  </TouchableOpacity>
);

const NavItem: React.FC<NavItemProps> = ({ title, icon }) => (
  <TouchableOpacity style={styles.navItem}>
    <Image source={icon} style={styles.navIcon} />
    <Text style={styles.navText}>{title}</Text>
  </TouchableOpacity>
);

export default App;

